-- ----------------------------
-- Table structure for `aa_article`
-- ----------------------------
DROP TABLE IF EXISTS `aa_article`;
CREATE TABLE `aa_article` (
  `article_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '',
  `summary` varchar(256) NOT NULL DEFAULT '' COMMENT '摘要',
  `content` text NOT NULL,
  `time` int(11) unsigned NOT NULL DEFAULT '0',
  `image` varchar(255) NOT NULL DEFAULT '',
  `category_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '属于哪个分类',
  `click_number` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '点击量',
  `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '1:正常 0：回收站',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`article_id`),
  KEY `title` (`title`,`time`,`category_id`) USING BTREE,
  KEY `time` (`time`) USING BTREE,
  KEY `category_id` (`category_id`) USING BTREE,
  KEY `status` (`status`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=46 DEFAULT CHARSET=utf8 COMMENT='博客文章表';

-- ----------------------------
-- Records of aa_article
-- ----------------------------
INSERT INTO `aa_article` VALUES ('1', 'html定义css', 'html定义css', '&lt;p style=&quot;color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在 html 可以使用如下格式定义 css&lt;/p&gt;&lt;p style=&quot;color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;lt;style type=&amp;quot;text/css&amp;quot;&amp;gt;&lt;br/&gt;&amp;lt;!--&lt;br/&gt;.STYLE1 {font-family: Arial, Helvetica, sans-serif}&lt;br/&gt;--&amp;gt;&lt;br/&gt;&amp;lt;/style&amp;gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516175713', '151617571359.jpg', '5', '836', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('2', '公共CSS写法', '公共CSS写法', '&lt;pre class=&quot;brush:css;toolbar:false&quot;&gt;/*公共css*/\r\n\r\n*&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;font-family:&amp;nbsp;&amp;quot;Microsoft&amp;nbsp;YaHei&amp;quot;,&amp;nbsp;&amp;quot;Helvetica&amp;nbsp;Neue&amp;quot;,&amp;nbsp;Helvetica,&amp;nbsp;STHeiTi,&amp;nbsp;sans-serif;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border-radius:&amp;nbsp;0px;\r\n}\r\n\r\nhtml&amp;nbsp;{}\r\n\r\nbody&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*background:&amp;nbsp;#EFEFEF;*/\r\n}\r\n\r\n.container&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;width:&amp;nbsp;1100px&amp;nbsp;!important;\r\n}\r\n\r\n.btn-warning&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#fff;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background:&amp;nbsp;#ff6724;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border:&amp;nbsp;none;\r\n}\r\n\r\n.load-more&amp;nbsp;.btn-warning&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#ff6724;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background:&amp;nbsp;#fff;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border:&amp;nbsp;none;\r\n}\r\n\r\n.load-more&amp;nbsp;.btn-warning:hover&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#fff;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background:&amp;nbsp;#ff6724&amp;nbsp;!important;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*background:&amp;nbsp;#eee&amp;nbsp;!important;*/\r\n}\r\n\r\n#no-more-article-list&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;display:&amp;nbsp;none;\r\n}\r\n\r\n.white&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background:&amp;nbsp;#fff;\r\n}\r\n\r\n.text-white&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#fff;\r\n}\r\n\r\n.text-info&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#ff6724;\r\n}\r\n\r\n.text-warning&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#ff6724;\r\n}\r\n\r\nhr&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-left:&amp;nbsp;23px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-right:&amp;nbsp;20px;\r\n}\r\n\r\n\r\n/*清除原生bootstrap内边距，外边据*/\r\n\r\n.container&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;padding-left:&amp;nbsp;0px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;padding-right:&amp;nbsp;0px;\r\n}\r\n\r\n.col-xs-1,&amp;nbsp;.col-sm-1,&amp;nbsp;.col-md-1,&amp;nbsp;.col-lg-1,&amp;nbsp;.col-xs-2,&amp;nbsp;.col-sm-2,&amp;nbsp;.col-md-2,&amp;nbsp;.col-lg-2,&amp;nbsp;.col-xs-3,&amp;nbsp;.col-sm-3,&amp;nbsp;.col-md-3,&amp;nbsp;.col-lg-3,&amp;nbsp;.col-xs-4,&amp;nbsp;.col-sm-4,&amp;nbsp;.col-md-4,&amp;nbsp;.col-lg-4,&amp;nbsp;.col-xs-5,&amp;nbsp;.col-sm-5,&amp;nbsp;.col-md-5,&amp;nbsp;.col-lg-5,&amp;nbsp;.col-xs-6,&amp;nbsp;.col-sm-6,&amp;nbsp;.col-md-6,&amp;nbsp;.col-lg-6,&amp;nbsp;.col-xs-7,&amp;nbsp;.col-sm-7,&amp;nbsp;.col-md-7,&amp;nbsp;.col-lg-7,&amp;nbsp;.col-xs-8,&amp;nbsp;.col-sm-8,&amp;nbsp;.col-md-8,&amp;nbsp;.col-lg-8,&amp;nbsp;.col-xs-9,&amp;nbsp;.col-sm-9,&amp;nbsp;.col-md-9,&amp;nbsp;.col-lg-9,&amp;nbsp;.col-xs-10,&amp;nbsp;.col-sm-10,&amp;nbsp;.col-md-10,&amp;nbsp;.col-lg-10,&amp;nbsp;.col-xs-11,&amp;nbsp;.col-sm-11,&amp;nbsp;.col-md-11,&amp;nbsp;.col-lg-11,&amp;nbsp;.col-xs-12,&amp;nbsp;.col-sm-12,&amp;nbsp;.col-md-12,&amp;nbsp;.col-lg-12&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;padding-left:&amp;nbsp;0px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;padding-right:&amp;nbsp;0px;\r\n}\r\n\r\n.row&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-left:&amp;nbsp;0px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-right:&amp;nbsp;0px;\r\n}\r\n\r\nh1,&amp;nbsp;h2,&amp;nbsp;h3,&amp;nbsp;h4,&amp;nbsp;h5,&amp;nbsp;h6&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-top:&amp;nbsp;10px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-bottom:&amp;nbsp;10px;\r\n}\r\n\r\n.btn&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border-radius:&amp;nbsp;0px;\r\n}\r\n\r\n\r\n/*分页样式*/\r\n\r\na:hover&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;text-decoration:&amp;nbsp;underline;\r\n}\r\n\r\n\r\n/*文章内容里面图片最大宽度100%*/\r\n\r\nli&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;list-style:&amp;nbsp;none;\r\n}\r\n\r\na&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;text-decoration:&amp;nbsp;none;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#1a1a1a;\r\n}\r\n\r\n\r\n/*顶部*/\r\n\r\n#top&amp;nbsp;{}\r\n\r\n#top&amp;nbsp;.input-group&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;height:&amp;nbsp;45px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border:&amp;nbsp;3px&amp;nbsp;solid&amp;nbsp;#ff9400;\r\n}\r\n\r\n#top&amp;nbsp;input&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;height:&amp;nbsp;45px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border-radius:&amp;nbsp;0px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border:&amp;nbsp;0px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/*去除input选中后的蓝边*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;outline:&amp;nbsp;none;\r\n}\r\n\r\n#top&amp;nbsp;button&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;height:&amp;nbsp;45px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#fff;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background-color:&amp;nbsp;#ff9400;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border-color:&amp;nbsp;#ff9400;\r\n}\r\n\r\n#top&amp;nbsp;.hot-search&amp;nbsp;a,&amp;nbsp;#top&amp;nbsp;.hot-search&amp;nbsp;span&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#999;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;font-size:&amp;nbsp;12px;\r\n}\r\n\r\n\r\n/*导航条*/\r\n\r\n#navigation&amp;nbsp;a&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;font-size:&amp;nbsp;16px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#fff;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;line-height:&amp;nbsp;37px;\r\n}\r\n\r\n#navigation&amp;nbsp;.col-xs-1:hover&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background:&amp;nbsp;#ff7900;\r\n}\r\n\r\n\r\n/*底部导航*/\r\n\r\n#footer&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background-color:&amp;nbsp;#363636;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;#ccc;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;height:&amp;nbsp;300px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;border-color:&amp;nbsp;none;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-top:&amp;nbsp;20px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;padding:&amp;nbsp;20px&amp;nbsp;0px&amp;nbsp;10px;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;margin-bottom:&amp;nbsp;0px;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516175692', '151617569292.jpg', '10', '246', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('3', 'js如何调用API接口', 'js如何调用API接口', '&lt;p style=&quot;text-align: center;&quot;&gt;这里我拿动脉网的首页文章做案例&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:js;toolbar:false&quot;&gt;/**\r\n&amp;nbsp;*&amp;nbsp;获取文章列表页数据\r\n&amp;nbsp;*&amp;nbsp;@Author&amp;nbsp;&amp;nbsp;&amp;nbsp;罗江涛\r\n&amp;nbsp;*&amp;nbsp;@DateTime&amp;nbsp;2016-03-24T16:10:45+0800\r\n&amp;nbsp;*/\r\nfunction&amp;nbsp;ajaxGetArticleList()&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$.ajax({\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;type:&amp;nbsp;&amp;quot;post&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;url:&amp;nbsp;&amp;quot;http://api.xxxx.net/index.php/Api/Article/getIndexArticleList&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;dataType:&amp;nbsp;&amp;quot;json&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;data:&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;token&amp;#39;:&amp;nbsp;token,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;page&amp;#39;:&amp;nbsp;page,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;categoryId&amp;#39;:&amp;nbsp;categoryId,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;},\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;success:&amp;nbsp;function(result)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(result.status&amp;nbsp;==&amp;nbsp;1)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;list&amp;nbsp;=&amp;nbsp;result.data.information;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for&amp;nbsp;(var&amp;nbsp;i&amp;nbsp;=&amp;nbsp;0;&amp;nbsp;i&amp;nbsp;&amp;lt;&amp;nbsp;list.length;&amp;nbsp;i++)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;组装数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(list[i].categoryId&amp;nbsp;!=&amp;nbsp;2992){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;articleListVue.todos.push({\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;page:&amp;nbsp;page,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;categoryName:&amp;nbsp;list[i].categoryName,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;title:&amp;nbsp;list[i].title,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;creatorName:&amp;nbsp;list[i].creatorName,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;summary:&amp;nbsp;list[i].summary,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;publishTime:&amp;nbsp;list[i].publishTime,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;logo:&amp;nbsp;list[i].logo,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;searchAuthorUrl:&amp;nbsp;index+&amp;quot;searchArticle/&amp;quot;+list[i].creatorName+&amp;quot;/searchtype/author&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;articleDetailUrl:&amp;nbsp;index+list[i].articleId+&amp;quot;.html&amp;quot;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(list.length&amp;nbsp;&amp;lt;&amp;nbsp;pageSize)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;showNoMoreData();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;page++;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(&amp;#39;#load-more-article-list&amp;#39;).button(&amp;#39;reset&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;没有结果\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;console.log(&amp;#39;get&amp;nbsp;data&amp;nbsp;false&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;},\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;error:&amp;nbsp;function(e)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;获取失败\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;console.log(&amp;#39;get&amp;nbsp;data&amp;nbsp;false&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516175676', '151617567618.jpg', '6', '302', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('4', 'Vue.js 是什么', '介绍 Vue.js 是什么', '&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Vue.js（读音 /vjuː/, 类似于&amp;nbsp;&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;view&lt;/span&gt;） 是一套构建用户界面的&amp;nbsp;&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;渐进式框架&lt;/span&gt;。与其他重量级框架不同的是，Vue 采用自底向上增量开发的设计。Vue 的核心库只关注视图层，并且非常容易学习，非常容易与其它库或已有项目整合。另一方面，Vue 完全有能力驱动采用&lt;a href=&quot;http://cn.vuejs.org/v2/guide/single-file-components.html&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131);&quot;&gt;单文件组件&lt;/a&gt;和&amp;nbsp;&lt;a href=&quot;http://github.com/vuejs/awesome-vue#libraries--plugins&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131);&quot;&gt;Vue 生态系统支持的库&lt;/a&gt;开发的复杂单页应用。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Vue.js 的目标是通过尽可能简单的 API 实现&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;响应的数据绑定&lt;/span&gt;和&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;组合的视图组件&lt;/span&gt;。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果你是有经验的前端开发者，想知道 Vue.js 与其它库/框架的区别，查看&lt;a href=&quot;http://cn.vuejs.org/v2/guide/comparison.html&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131);&quot;&gt;对比其它框架&lt;/a&gt;。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#起步&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h2 style=&quot;color: rgb(44, 62, 80); margin: 45px 0px 0.8em; padding-bottom: 0.7em; border-bottom: 1px solid rgb(221, 221, 221); z-index: -1;&quot;&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#起步&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-weight: normal; font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/a&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#起步&quot; class=&quot;headerlink&quot; title=&quot;起步&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-weight: 400;&quot;&gt;&lt;/a&gt;起步&lt;/h2&gt;&lt;p class=&quot;tip&quot; style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 2em; margin-bottom: 2em; padding: 12px 24px 12px 30px; position: relative; z-index: 1; border-left: 4px solid rgb(255, 102, 102); background-color: rgb(248, 248, 248); border-bottom-right-radius: 2px; border-top-right-radius: 2px; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal;&quot;&gt;官方指南假设你已有 HTML、CSS 和 JavaScript 中级前端知识。如果你是全新的前端开发者，将框架作为你的第一步可能不是最好的主意——掌握好基础知识再来！之前有其他框架的使用经验是有帮助的，但不是必需的。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;尝试 Vue.js 最简单的方法是使用&amp;nbsp;&lt;a href=&quot;https://jsfiddle.net/chrisvfritz/50wL7mdz/&quot; target=&quot;_blank&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131);&quot;&gt;JSFiddle Hello World 例子&lt;/a&gt;。你可以在浏览器新标签页中打开它，跟着我们学习一些基础示例。或者你也可以创建一个本地的&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;.html&lt;/code&gt;&amp;nbsp;文件，然后通过如下方式引入 Vue:&lt;/p&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;&amp;lt;script&amp;nbsp;src=&amp;quot;https://unpkg.com/vue/dist/vue.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;你可以查看&lt;a href=&quot;http://cn.vuejs.org/guide/installation.html&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131);&quot;&gt;安装指南&lt;/a&gt;来了解其他安装 Vue 的选项。请注意我们&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;不推荐&lt;/span&gt;新手直接使用&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;vue-cli&lt;/code&gt;，尤其是对 Node.js 构建工具不够了解的同学。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#声明式渲染&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h2 style=&quot;color: rgb(44, 62, 80); margin: 45px 0px 0.8em; padding-bottom: 0.7em; border-bottom: 1px solid rgb(221, 221, 221); z-index: -1;&quot;&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#声明式渲染&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-weight: normal; font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/a&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#声明式渲染&quot; class=&quot;headerlink&quot; title=&quot;声明式渲染&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-weight: 400;&quot;&gt;&lt;/a&gt;声明式渲染&lt;/h2&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Vue.js 的核心是一个允许你采用简洁的模板语法来声明式的将数据渲染进 DOM 的系统：&lt;/p&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;&amp;lt;div&amp;nbsp;id=&amp;quot;app&amp;quot;&amp;gt;{{&amp;nbsp;message&amp;nbsp;}}&amp;lt;/div&amp;gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;var&amp;nbsp;app&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Vue({el:&amp;nbsp;&amp;#39;#app&amp;#39;,data:&amp;nbsp;{message:&amp;nbsp;&amp;#39;Hello&amp;nbsp;Vue!&amp;#39;}})&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;Hello Vue!&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;我们已经生成了我们的第一个 Vue 应用！看起来这跟单单渲染一个字符串模板非常类似，但是 Vue.js 在背后做了大量工作。现在数据和 DOM 已经被绑定在一起，所有的元素都是&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;响应式的&lt;/span&gt;。我们如何知道？打开你的浏览器的控制台，并修改&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;app.message&lt;/code&gt;，你将看到上例相应地更新。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;除了绑定插入的文本内容，我们还可以采用这样的方式绑定 DOM 元素属性：&lt;/p&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;&amp;lt;div&amp;nbsp;id=&amp;quot;app-2&amp;quot;&amp;gt;&amp;lt;span&amp;nbsp;v-bind:title=&amp;quot;message&amp;quot;&amp;gt;Hover&amp;nbsp;your&amp;nbsp;mouse&amp;nbsp;over&amp;nbsp;me&amp;nbsp;for&amp;nbsp;a&amp;nbsp;few&amp;nbsp;seconds&amp;nbsp;to&amp;nbsp;see&amp;nbsp;my&amp;nbsp;dynamically&amp;nbsp;bound&amp;nbsp;title!&amp;lt;/span&amp;gt;&amp;lt;/div&amp;gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;var&amp;nbsp;app2&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Vue({el:&amp;nbsp;&amp;#39;#app-2&amp;#39;,data:&amp;nbsp;{message:&amp;nbsp;&amp;#39;You&amp;nbsp;loaded&amp;nbsp;this&amp;nbsp;page&amp;nbsp;on&amp;nbsp;&amp;#39;&amp;nbsp;+&amp;nbsp;new&amp;nbsp;Date()}})&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;Hover your mouse over me for a few seconds to see my dynamically bound title!&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;这里我们遇到点新东西。你看到的&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;v-bind&lt;/code&gt;&amp;nbsp;属性被称为&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;指令&lt;/span&gt;。指令带有前缀&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;v-&lt;/code&gt;，以表示它们是 Vue.js 提供的特殊属性。可能你已经猜到了，它们会在渲染过的 DOM 上应用特殊的响应式行为。这个指令的简单含义是说：将这个元素节点的&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;title&lt;/code&gt;&amp;nbsp;属性和 Vue 实例的&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;message&lt;/code&gt;&amp;nbsp;属性绑定到一起。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;你再次打开浏览器的控制台输入&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;app2.message = &amp;#39;some new message&amp;#39;&lt;/code&gt;，你就会再一次看到这个绑定了&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;title&lt;/code&gt;属性的HTML已经进行了更新。&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#条件与循环&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;h2 style=&quot;color: rgb(44, 62, 80); margin: 45px 0px 0.8em; padding-bottom: 0.7em; border-bottom: 1px solid rgb(221, 221, 221); z-index: -1;&quot;&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#条件与循环&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-weight: normal; font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;/a&gt;&lt;a href=&quot;http://cn.vuejs.org/v2/guide/#条件与循环&quot; class=&quot;headerlink&quot; title=&quot;条件与循环&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131); font-weight: 400;&quot;&gt;&lt;/a&gt;条件与循环&lt;/h2&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;控制切换一个元素的显示也相当简单：&lt;/p&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;&amp;lt;div&amp;nbsp;id=&amp;quot;app-3&amp;quot;&amp;gt;&amp;lt;p&amp;nbsp;v-if=&amp;quot;seen&amp;quot;&amp;gt;Now&amp;nbsp;you&amp;nbsp;see&amp;nbsp;me&amp;lt;/p&amp;gt;&amp;lt;/div&amp;gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;var&amp;nbsp;app3&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Vue({el:&amp;nbsp;&amp;#39;#app-3&amp;#39;,data:&amp;nbsp;{seen:&amp;nbsp;true}})&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;Now you see me&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;继续在控制台设置&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;app3.seen = false&lt;/code&gt;，你会发现 “Now you see me” 消失了。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;这个例子演示了我们不仅可以绑定 DOM 文本到数据，也可以绑定 DOM&amp;nbsp;&lt;span style=&quot;font-weight: 600; color: rgb(44, 62, 80);&quot;&gt;结构&lt;/span&gt;到数据。而且，Vue.js 也提供一个强大的过渡效果系统，可以在 Vue 插入/删除元素时自动应用&lt;a href=&quot;http://cn.vuejs.org/v2/guide/transitions.html&quot; style=&quot;text-decoration: none; color: rgb(66, 185, 131);&quot;&gt;过渡效果&lt;/a&gt;。&lt;/p&gt;&lt;p style=&quot;word-spacing: 0.05em; line-height: 1.6em; margin-top: 1.2em; margin-bottom: -1.2em; padding-bottom: 1.2em; position: relative; z-index: 1; color: rgb(52, 73, 94); font-family: &amp;quot;Source Sans Pro&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, Arial, sans-serif; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;也有一些其它指令，每个都有特殊的功能。例如，&amp;nbsp;&lt;code style=&quot;font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; background-color: rgb(248, 248, 248); -webkit-font-smoothing: initial; color: rgb(233, 105, 0); padding: 3px 5px; margin: 0px 2px; border-radius: 2px; white-space: nowrap;&quot;&gt;v-for&lt;/code&gt;&amp;nbsp;指令可以绑定数据到数组来渲染一个列表：&lt;/p&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;&amp;lt;div&amp;nbsp;id=&amp;quot;app-4&amp;quot;&amp;gt;&amp;lt;ol&amp;gt;&amp;lt;li&amp;nbsp;v-for=&amp;quot;todo&amp;nbsp;in&amp;nbsp;todos&amp;quot;&amp;gt;{{&amp;nbsp;todo.text&amp;nbsp;}}&amp;lt;/li&amp;gt;&amp;lt;/ol&amp;gt;&amp;lt;/div&amp;gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;table width=&quot;776&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;width: 776px; border-collapse: collapse; padding: 0px; margin: 0px;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;code&quot; style=&quot;border-collapse: collapse; padding: 0px; margin: 0px;&quot; width=&quot;776&quot;&gt;&lt;pre style=&quot;color: rgb(82, 82, 82); font-family: &amp;quot;Roboto Mono&amp;quot;, Monaco, courier, monospace; font-size: 0.8em; -webkit-font-smoothing: initial; padding: 1.2em 1.4em; line-height: 1.5em; margin-top: 0px; margin-bottom: 0px;&quot;&gt;var&amp;nbsp;app4&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Vue({el:&amp;nbsp;&amp;#39;#app-4&amp;#39;,data:&amp;nbsp;{todos:&amp;nbsp;[{&amp;nbsp;text:&amp;nbsp;&amp;#39;Learn&amp;nbsp;JavaScript&amp;#39;&amp;nbsp;},{&amp;nbsp;text:&amp;nbsp;&amp;#39;Learn&amp;nbsp;Vue&amp;#39;&amp;nbsp;},{&amp;nbsp;text:&amp;nbsp;&amp;#39;Build&amp;nbsp;something&amp;nbsp;awesome&amp;#39;&amp;nbsp;}]}})&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516175656', '151617565644.jpg', '6', '446', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('5', '阿里云CentOS搭建LAMP 环境', '阿里云下CentOS如何搭建LAMP 环境', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;阿里云论坛上的资源很丰富，查了下安装LAMP环境，很多基础性的东西已经内置了，比如防火墙的配置和端口的开启，统统不需要再做操作，所以安装下来也很简单：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;line-height: 21.6px; font-size: 12px; color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;1、MySQL的安装&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;MySQL安装涉及到的软件包：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;mysql: MySQL客户端以及基础库文件。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;mysql-libs: MySQL客户端所必需的库文件。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;mysql-server: MySQL服务器端以及相关文件。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;安装命令：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;yum intall mysql mysql-libs mysql-server&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;line-height: 21.6px; font-size: 12px; color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;2、PHP的安装&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;预备工作：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;同样需要预先安装EPEL软件。这里不安装也不影响此部分，但是在以后的一些教程中可能会安装一些不在标准软件源中提供的软件，所以在此给出安装命令。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;安装命令：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;wget&amp;nbsp;&lt;a href=&quot;http://mirrors.sohu.com/fedora-epel/6/i386/epel-release-6-8.noarch.rpm&quot; style=&quot;color: rgb(0, 102, 153); text-decoration: none;&quot;&gt;http://mirrors.sohu.com/fedora-epel/6/i386/epel-release-6-8.noarch.rpm&lt;/a&gt;&lt;br/&gt;rpm -ivh epel-release-6-8.noarch.rpm&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;PHP安装涉及软件包：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php.x86_64 : 服务器端PHP程序&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-cli.x86_64 : PHP的命令行工具&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-common.x86_64 : PHP的公用文件&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-fpm.x86_64 : PHP的Fastcgi进程管理模块。主要用于配置Nginx中，如果配置为 Apache 则可不装字模块&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-gd.x86_64 : PHP的GD库，主要用于图像处理&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-imap.x86_64 : PHP的IMAP模块&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-ldap.x86_64 : PHP的LADP模块&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-mbstring.x86_64 : PHP的多字节字符处理模块&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-snmp.x86_64 : PHP的SNMP协议模块&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-xml.x86_64 : PHP的XML模块&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php-zts.x86_64 : PHP的线程安全模块。这个是用于Apache的，工作于Apache的Worker模式下。在此列出来仅为讨论子模块用途，具体效果未知，安装命令中也不会进行安装。希望有了解的人可以发信息个我讨论一下此模块的与Apache的工作效果。不过Centos中既然提供了此模块，相信其稳定性还是通过了验证的。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;安装命令：&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;yum install php php-cli php-common php-fpm php-gd php-imap php-ldap php-mbstring php-snmp php-xml&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;line-height: 21.6px; font-size: 12px; color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;3、Apache的安装&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;我购置的服务器默认已安装，无需再次安装&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;如果需要安装的话，命令是：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;yum install httpd&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;line-height: 21.6px; font-size: 12px; color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;4、启动服务&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;service mysqld start&lt;br/&gt;service httpd start&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;打开浏览器，输入IP此时就应该能看到欢迎页了。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&lt;span style=&quot;line-height: 21.6px; font-size: 12px; color: rgb(255, 0, 0);&quot;&gt;阿里云服务器下安装配置 vsftpd —— 基于CentOS 6.3&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;1、更新yum源&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;我是直接 yum update 更新的&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;2、安装vsftp&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;使用yum命令安装vsftpd&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;#yum install vsftpd -y&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;3、添加ftp帐号和目录&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;先检查一下nologin的位置，通常在/usr/sbin/nologin或者/sbin/nologin下。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;使用下面的命令创建帐户，该命令指定了/www/wwwroot为用户pwftp的家目录，您可以自己定义帐户名和目录：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;#useradd -d /www/wwwroot -s /sbin/nologin pwftp&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;修改该帐户密码:&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;#passwd pwftp&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;修改指定目录的权限&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;#chown -R pwftp:pwftp /www/wwwroot&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;这里直接这么操作要报错，要在执行这一条命令之前做如下操作：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;mkdir www&lt;br/&gt;cd www&lt;br/&gt;mkdir wwwroot&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;4、配置vsftp&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;编辑vsftpd配置文件，命令如下：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;#vi /etc/vsftpd/vsftpd.conf&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;将配置文件中 “anonymous_enable=YES ” 改为 “anonymous_enable=NO”&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;取消如下配置前的注释符号：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;local_enable=YES&lt;br/&gt;write_enable=YES&lt;br/&gt;chroot_local_user=YES&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;保存修改，按ESC键，输入：wq&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;5、修改shell配置&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;vi编辑/etc/shells，如果该文件里没有/usr/sbin/nologin或者/sbin/nologin（具体看当前系统配置）则追加进去&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;6、启动vsftp服务并测试登录&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;使用命令启动vsftpd服务：&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;#service vsftpd start&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;然后用帐号 pwftp 测试下是否可以登陆 ftp。目录是/www/wwwroot。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 5px 0px; font-size: 14px; font-family: tahoma, arial, 宋体; line-height: 25.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在这里我直接用客户端软件FileZilla Client登录，经测试，完全正确。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516157582', '151615758255.jpg', '7', '722', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('8', '变态产品经理的需求下写出的sql', '产品经理VS程序员', '&lt;p&gt;程序员和产品经理果然是有仇啊&lt;/p&gt;&lt;p&gt;这只是一条sql语句。。。&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;&amp;nbsp;//&amp;nbsp;找出活动列表数据，未开始在前，已开始在中间，结束在后面\r\n$sql&amp;nbsp;=&amp;nbsp;&amp;quot;(&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;(select&amp;nbsp;c.campaignId,&amp;nbsp;c.title,&amp;nbsp;c.summary,&amp;nbsp;c.startTime,&amp;nbsp;c.endTime,&amp;nbsp;c.address,&amp;nbsp;c.logo,&amp;nbsp;c.link,&amp;nbsp;c.price,&amp;nbsp;c.number&amp;nbsp;from&amp;nbsp;campaign&amp;nbsp;c\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;where&amp;nbsp;c.status&amp;nbsp;=&amp;nbsp;1\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;and&amp;nbsp;c.endTime&amp;nbsp;&amp;gt;&amp;nbsp;now()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;and&amp;nbsp;c.startTime&amp;nbsp;&amp;gt;&amp;nbsp;now()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;order&amp;nbsp;by&amp;nbsp;c.startTime&amp;nbsp;desc,&amp;nbsp;c.endTime&amp;nbsp;desc)&amp;nbsp;a1&amp;nbsp;order&amp;nbsp;by&amp;nbsp;a1.startTime&amp;nbsp;desc,&amp;nbsp;a1.endTime&amp;nbsp;desc)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;union\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;(&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from(&amp;nbsp;&amp;nbsp;&amp;nbsp;select&amp;nbsp;c.campaignId,&amp;nbsp;c.title,&amp;nbsp;c.summary,&amp;nbsp;c.startTime,&amp;nbsp;c.endTime,&amp;nbsp;c.address,&amp;nbsp;c.logo,&amp;nbsp;c.link,&amp;nbsp;c.price,&amp;nbsp;c.number&amp;nbsp;from&amp;nbsp;campaign&amp;nbsp;c\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;where&amp;nbsp;c.status&amp;nbsp;=&amp;nbsp;1\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;and&amp;nbsp;c.endTime&amp;nbsp;&amp;gt;&amp;nbsp;now()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;and&amp;nbsp;c.startTime&amp;nbsp;&amp;lt;&amp;nbsp;now()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;order&amp;nbsp;by&amp;nbsp;c.startTime&amp;nbsp;desc,&amp;nbsp;c.endTime&amp;nbsp;desc)&amp;nbsp;a2&amp;nbsp;order&amp;nbsp;by&amp;nbsp;a2.startTime&amp;nbsp;desc,&amp;nbsp;a2.endTime&amp;nbsp;desc)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;union\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;(&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;&amp;nbsp;(select&amp;nbsp;c.campaignId,&amp;nbsp;c.title,&amp;nbsp;c.summary,&amp;nbsp;c.startTime,&amp;nbsp;c.endTime,&amp;nbsp;c.address,&amp;nbsp;c.logo,&amp;nbsp;c.link,&amp;nbsp;c.price,&amp;nbsp;c.number&amp;nbsp;from&amp;nbsp;campaign&amp;nbsp;c\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;where&amp;nbsp;c.status&amp;nbsp;=&amp;nbsp;1\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;and&amp;nbsp;c.endTime&amp;nbsp;&amp;lt;&amp;nbsp;now()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;and&amp;nbsp;c.startTime&amp;nbsp;&amp;lt;&amp;nbsp;now()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;order&amp;nbsp;by&amp;nbsp;c.startTime&amp;nbsp;desc,&amp;nbsp;c.endTime&amp;nbsp;desc)&amp;nbsp;a3&amp;nbsp;order&amp;nbsp;by&amp;nbsp;a3.startTime&amp;nbsp;desc,&amp;nbsp;a3.endTime&amp;nbsp;desc)&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;limit&amp;nbsp;{$start},&amp;nbsp;{$limit}&amp;quot;;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516157596', '151615759635.jpg', '8', '680', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('9', 'PHP如何编写API接口', '\'\'', '&lt;p&gt;首先定义一个标准的基本数据结构&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;返回一个标准的基本数据结构\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$status&amp;nbsp;&amp;nbsp;&amp;nbsp;状态值&amp;nbsp;0&amp;nbsp;不正常，1正常\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$message&amp;nbsp;&amp;nbsp;返回消息\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$error&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;错误提示\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;array&amp;nbsp;$data&amp;nbsp;返回给请求者的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array&amp;nbsp;标准数据结构\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@author&amp;nbsp;罗江涛\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;protected&amp;nbsp;function&amp;nbsp;getStandResult($status,&amp;nbsp;$message,&amp;nbsp;$error,&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;array())\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;status&amp;quot;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;$status,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;message&amp;quot;&amp;nbsp;=&amp;gt;&amp;nbsp;$message,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;error&amp;quot;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;$error,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;data&amp;quot;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;$data,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$result;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;打印json，统一输出，方便插入值&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;打印json，统一输出，方便插入值\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;$result\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@author&amp;nbsp;罗江涛\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;protected&amp;nbsp;function&amp;nbsp;json($result)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//输出头\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!C(&amp;quot;SHOW_PAGE_TRACE&amp;quot;))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;header(&amp;#39;Content-type:&amp;nbsp;application/json&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;输出json格式的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;json_encode($result);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;使用这两个方法，输出json格式数据&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;获取首页文章列表数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@author&amp;nbsp;罗江涛\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@DateTime&amp;nbsp;2016-01-12T15:18:22+0800\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;getIndexArticleList()\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;接收参数，用于分页\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$page&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;I(&amp;#39;page&amp;#39;,&amp;nbsp;0,&amp;nbsp;&amp;#39;intval&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$categoryId&amp;nbsp;=&amp;nbsp;I(&amp;#39;categoryId&amp;#39;,&amp;nbsp;0,&amp;nbsp;&amp;#39;intval&amp;#39;);\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//定义数据模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$db&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;M(&amp;quot;article&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$adminDb&amp;nbsp;=&amp;nbsp;M(&amp;quot;admin&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$limit&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;C(&amp;#39;PAGE_SIZE&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$where&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;quot;&amp;nbsp;1=1&amp;nbsp;&amp;quot;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$where&amp;nbsp;.=&amp;nbsp;&amp;quot;&amp;nbsp;and&amp;nbsp;(status&amp;nbsp;=&amp;nbsp;1)&amp;nbsp;and&amp;nbsp;publishTime&amp;nbsp;order(&amp;#39;publishTime&amp;nbsp;DESC&amp;#39;)-&amp;gt;field(&amp;#39;articleId,&amp;nbsp;title,&amp;nbsp;summary,&amp;nbsp;logo,&amp;nbsp;publishTime,&amp;nbsp;categoryId,&amp;nbsp;adminId,&amp;nbsp;editorId&amp;#39;)-&amp;gt;page($page)-&amp;gt;where($where)-&amp;gt;limit($limit)-&amp;gt;select();\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;统计文章数\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;$db-&amp;gt;where($where)-&amp;gt;count();\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;如果查询结果为空\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!$article)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$this-&amp;gt;getStandResult(0,&amp;nbsp;&amp;quot;没有查询到结果&amp;quot;,&amp;nbsp;&amp;quot;&amp;quot;,&amp;nbsp;&amp;quot;&amp;quot;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;json($result);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$categoryDb&amp;nbsp;=&amp;nbsp;M(&amp;#39;category&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach&amp;nbsp;($article&amp;nbsp;as&amp;nbsp;$key&amp;nbsp;=&amp;gt;&amp;nbsp;$value)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;获取文章分类名称\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$article[$key][&amp;#39;categoryName&amp;#39;]&amp;nbsp;=&amp;nbsp;$categoryDb-&amp;gt;where(&amp;quot;categoryId=&amp;#39;{$value[&amp;#39;categoryId&amp;#39;]}&amp;#39;&amp;quot;)-&amp;gt;getField(&amp;#39;categoryName&amp;#39;);\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//转换时间，转化为几分钟前，几小时前\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$article[$key][&amp;#39;publishTime&amp;#39;]&amp;nbsp;=&amp;nbsp;getTimeDifference($value[&amp;#39;publishTime&amp;#39;]);\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//判断摘要是否有数据，如果没有数据，就把内容里的作为摘要显示\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!empty($value[&amp;#39;summary&amp;#39;]))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;截取前70个字\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$article[$key][&amp;#39;summary&amp;#39;]&amp;nbsp;=&amp;nbsp;msubstr($value[&amp;#39;summary&amp;#39;],&amp;nbsp;0,&amp;nbsp;70,&amp;nbsp;&amp;quot;utf-8&amp;quot;,&amp;nbsp;true);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;没有摘要，从文章内容里面找前70个字作为摘要\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$content&amp;nbsp;=&amp;nbsp;$db-&amp;gt;where(&amp;quot;articleId&amp;nbsp;=&amp;nbsp;&amp;#39;{$value[&amp;#39;articleId&amp;#39;]}&amp;#39;&amp;quot;)-&amp;gt;getField(&amp;#39;content&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;去掉html标签\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$content&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;strip_tags($content);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$article[$key][&amp;#39;summary&amp;#39;]&amp;nbsp;=&amp;nbsp;msubstr($content,&amp;nbsp;0,&amp;nbsp;70,&amp;nbsp;&amp;quot;utf-8&amp;quot;,&amp;nbsp;true);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//logo图片地址补全\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$article&amp;nbsp;=&amp;nbsp;checkLogo($article);\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;找出文章作者\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$article&amp;nbsp;=&amp;nbsp;$this-&amp;gt;findCreatorName($article);\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;组装并输出标准数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$this-&amp;gt;getStandResult(1,&amp;nbsp;&amp;quot;查询首页文章列表数据成功&amp;quot;,&amp;nbsp;&amp;quot;&amp;quot;,&amp;nbsp;array(&amp;quot;information&amp;quot;&amp;nbsp;=&amp;gt;&amp;nbsp;$article,&amp;nbsp;&amp;quot;count&amp;quot;&amp;nbsp;=&amp;gt;&amp;nbsp;$count));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;json($result);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516157567', '151615756795.jpg', '4', '26721', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('10', 'API接口文档案例', 'API接口文档案例，如何编写API文档', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;就拿动脉网的首页文章数据举例吧&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;我使用的工具是&amp;nbsp;&lt;a href=&quot;http://www.showdoc.cc/&quot;&gt;http://www.showdoc.cc/&lt;/a&gt;&amp;nbsp;感觉还不错的样子&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;简要描述：&lt;/strong&gt;&lt;/p&gt;&lt;ul style=&quot;padding: 0px; margin-bottom: 10px; margin-left: 25px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;获取首页文章列表&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;请求URL：&lt;/strong&gt;&lt;/p&gt;&lt;ul style=&quot;padding: 0px; margin-bottom: 10px; margin-left: 25px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;code style=&quot;padding: 2px 4px; font-family: Monaco, Menlo, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 12px; color: rgb(221, 17, 68); border-radius: 3px; white-space: nowrap; border: 1px solid rgb(225, 225, 232); background-color: rgb(247, 247, 249);&quot;&gt;http://vcbeatapi.luojiangtao.com/index.php/Api/Article/getIndexArticleList&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;请求方式：&lt;/strong&gt;&lt;/p&gt;&lt;ul style=&quot;padding: 0px; margin-bottom: 10px; margin-left: 25px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;POST ，GET 可选（建议用POST）&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;参数：&lt;/strong&gt;&lt;/p&gt;&lt;table class=&quot;table table-bordered table-hover&quot; width=&quot;1589&quot; style=&quot;width: 568px;&quot;&gt;&lt;thead&gt;&lt;tr style=&quot;color: rgb(255, 255, 255); background-color: rgb(0, 136, 204);&quot; class=&quot;firstRow&quot;&gt;&lt;th style=&quot;padding: 8px; line-height: 20px; vertical-align: bottom; border-top-width: 0px; border-top-left-radius: 4px; min-width: 77px;&quot;&gt;参数名&lt;/th&gt;&lt;th style=&quot;padding: 8px; line-height: 20px; vertical-align: bottom; border-top-width: 0px; min-width: 77px;&quot;&gt;必选&lt;/th&gt;&lt;th style=&quot;padding: 8px; line-height: 20px; vertical-align: bottom; border-top-width: 0px; min-width: 77px;&quot;&gt;类型&lt;/th&gt;&lt;th style=&quot;padding: 8px; line-height: 20px; vertical-align: bottom; border-top-width: 0px; border-top-right-radius: 4px; min-width: 77px;&quot;&gt;说明&lt;/th&gt;&lt;/tr&gt;&lt;/thead&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;categoryId&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;否&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;string&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;所属栏目，切换栏目时需要&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;token&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;否&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;string&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;用户识别唯一编码&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top; border-bottom-left-radius: 4px;&quot;&gt;page&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;否&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top;&quot;&gt;string&lt;/td&gt;&lt;td style=&quot;padding: 8px; line-height: 20px; vertical-align: top; border-bottom-right-radius: 4px;&quot;&gt;第几页，默认1&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 10px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, 微软雅黑, Tahoma, Arial, Helvetica, STHeiti; line-height: 27.2px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;返回示例&lt;/strong&gt;&lt;/p&gt;&lt;pre style=&quot;padding: 9.5px; font-family: Monaco, Menlo, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 13px; color: rgb(51, 51, 51); border-radius: 4px; margin-top: 0px; margin-bottom: 10px; line-height: 20px; word-break: break-all; word-wrap: break-word; white-space: pre-wrap; border: 1px solid rgba(0, 0, 0, 0.14902); background-color: rgb(245, 245, 245);&quot;&gt;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;status&amp;quot;:&amp;nbsp;1,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;message&amp;quot;:&amp;nbsp;&amp;quot;获取数据成功&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;error&amp;quot;:&amp;nbsp;&amp;quot;&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;data&amp;quot;:&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;information&amp;quot;:&amp;nbsp;[\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;articleId&amp;quot;:&amp;nbsp;&amp;quot;28421&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;title&amp;quot;:&amp;nbsp;&amp;quot;fdssdf&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;creatorName&amp;quot;:&amp;nbsp;&amp;quot;admin&amp;quot;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;summary&amp;quot;:&amp;nbsp;&amp;quot;asdf1&amp;quot;,\r