INSERT INTO `aa_article` VALUES ('11', 'luojiangtaoPHP框架目录结构', 'luojiangtaoPHP框架目录结构', '&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;目录结构清晰，我要的，现在就要&lt;/p&gt;&lt;pre class=&quot;brush:cpp;toolbar:false&quot;&gt;├──&amp;nbsp;Admin&amp;nbsp;&amp;nbsp;&amp;nbsp;后台分组\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Config\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;config.php&amp;nbsp;后台配置文件\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Controller&amp;nbsp;用户自定义控制器目录\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;IndexController.class.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;View\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Index&amp;nbsp;模版页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;index.html\r\n├──&amp;nbsp;admin.php&amp;nbsp;后台入口\r\n├──&amp;nbsp;Common公共目录\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Config&amp;nbsp;公共配置\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;config.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Lib&amp;nbsp;公共函数\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;function.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Model&amp;nbsp;公共模型\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;CategoryModel.class.php\r\n├──&amp;nbsp;Index&amp;nbsp;前台分组\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Config&amp;nbsp;配置文件\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;config.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Controller&amp;nbsp;&amp;nbsp;用户自定义控制器目录\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;EmptyController.class.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;IndexController.class.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;View&amp;nbsp;模版页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Index\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;index.html\r\n├──&amp;nbsp;index.php&amp;nbsp;前台入口\r\n├──&amp;nbsp;luojiangtaoPHP&amp;nbsp;框架\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Config&amp;nbsp;框架配置\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;config.php\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Data&amp;nbsp;框架模版页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;View\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;error.html&amp;nbsp;错误页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;halt.html&amp;nbsp;异常页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;notice.html&amp;nbsp;警告页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;success.html&amp;nbsp;成功页面\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Extends&amp;nbsp;扩展类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Org&amp;nbsp;第三方类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Smarty.class.php&amp;nbsp;模版标签解析类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Tool&amp;nbsp;工具类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Code.class.php&amp;nbsp;验证码类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Model.class.php&amp;nbsp;数据库类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Page.class.php&amp;nbsp;分页类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Upload.class.php&amp;nbsp;上传类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Lib&amp;nbsp;类库\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Core&amp;nbsp;核心类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Application.class.php&amp;nbsp;框架第二层处理类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Controller.class.php&amp;nbsp;控制器父类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Log.class.php&amp;nbsp;日志类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Function&amp;nbsp;公共方法文件夹\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;function.php&amp;nbsp;公共方法文件\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;luojiangtaoPHP.php&amp;nbsp;框架入口文件，必须引入\r\n├──&amp;nbsp;Temp\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;~boot.php&amp;nbsp;聚合类\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Cache&amp;nbsp;静态缓存文件夹\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;d41d8cd98f00b204e9800998ecf8427e.html\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;Compile&amp;nbsp;编译文件夹\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;│&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;31c163238dc9d102b11dcc5c7e0fff4e.html\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;└──&amp;nbsp;Log&amp;nbsp;日志文件夹\r\n│&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;2016_08_05.log\r\n└──&amp;nbsp;Upload&amp;nbsp;文件上传目录\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;├──&amp;nbsp;147140816195.jpg&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516157538', '151615753836.jpg', '4', '707', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('12', 'centos7 lnmp环境搭建', 'centos7 lnmp环境搭建', '&lt;h2 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、先做准备工作：&lt;/h2&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;①、安装LNMP环境所需的基础组件：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;yum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;yum-fastestmirroryum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;glibc-static&amp;nbsp;libstdc++-static&amp;nbsp;glibc-devel&amp;nbsp;GeoIP&amp;nbsp;GeoIP-devel&amp;nbsp;sqlite-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;patch&amp;nbsp;make&amp;nbsp;flex&amp;nbsp;bison&amp;nbsp;tar&amp;nbsp;pcre&amp;nbsp;pcre-devel&amp;nbsp;screen&amp;nbsp;vsftpd&amp;nbsp;ftp&amp;nbsp;mailx&amp;nbsp;jwhois&amp;nbsp;postfix&amp;nbsp;cronie\r\nyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;libtool&amp;nbsp;libtool-libs&amp;nbsp;kernel-devel&amp;nbsp;autoconf\r\nyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;libjpeg&amp;nbsp;libjpeg-devel&amp;nbsp;libpng&amp;nbsp;libpng-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;libtiff&amp;nbsp;libtiff-devel&amp;nbsp;gettext&amp;nbsp;gettext-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;freetype&amp;nbsp;freetype-devel&amp;nbsp;libxml2&amp;nbsp;libxml2-devel&amp;nbsp;zlib&amp;nbsp;zlib-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;file&amp;nbsp;glib2&amp;nbsp;glib2-devel&amp;nbsp;bzip2&amp;nbsp;openldap-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;bzip2-devel&amp;nbsp;ncurses&amp;nbsp;ncurses-devel&amp;nbsp;curl&amp;nbsp;curl-devel&amp;nbsp;e2fsprogs\r\nyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;e2fsprogs-devel&amp;nbsp;krb5-devel&amp;nbsp;libidn&amp;nbsp;libidn-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;openssl&amp;nbsp;openssl-devel&amp;nbsp;vim-minimal&amp;nbsp;unzip\r\nyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;libevent&amp;nbsp;mcrypt&amp;nbsp;mash&amp;nbsp;libssh2&amp;nbsp;libssh2-devel&amp;nbsp;libmetalink&amp;nbsp;libmetalink-devel&amp;nbsp;nghttp2\r\nyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;gd&amp;nbsp;recode&amp;nbsp;recode-devel&amp;nbsp;libatomic_ops-develyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;libXpm&amp;nbsp;libXpm-devel&amp;nbsp;gmp&amp;nbsp;gmp-devel&amp;nbsp;gd-develyum&amp;nbsp;-y&amp;nbsp;update\r\nexport&amp;nbsp;PHP_AUTOCONF=/usr/bin/autoconf\r\nexport&amp;nbsp;PHP_AUTOHEADER=/usr/bin/autoheader12345678910111213141516171234567891011121314151617&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;②、CentOS设置防火墙由&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;iptables&lt;/code&gt;改为&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;firewall&lt;/code&gt;，&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;firewall&lt;/code&gt;使用方法：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;#安装firewalldyum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;firewalld#开启firewalldsystemctl&amp;nbsp;start&amp;nbsp;firewalld#开机启动firewalldsystemctl&amp;nbsp;enable&amp;nbsp;firewalld#关闭firewalldsystemctl&amp;nbsp;stop&amp;nbsp;firewalld#取消开机启动firewalldsystemctl&amp;nbsp;disable&amp;nbsp;firewalld#为public域开放tcp协议的8080端口firewall-cmd&amp;nbsp;--zone=public&amp;nbsp;--add-port=8080/tcp&amp;nbsp;--permanent#为public域添加http服务firewall-cmd&amp;nbsp;--zone=public&amp;nbsp;--add-service=http&amp;nbsp;--permanent#重启firewall服务firewall-cmd&amp;nbsp;--reload1234567891011121314151612345678910111213141516&lt;/pre&gt;&lt;hr/&gt;&lt;h2 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;2、安装MySQL：&lt;/h2&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;PS.官方有详细的安装说明，详情请戳：&lt;a href=&quot;http://dev.mysql.com/doc/refman/5.7/en/installing.html&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 102, 153); text-decoration: none; box-sizing: border-box;&quot;&gt;MySQL安装方法详解&lt;/a&gt;&lt;/p&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;①、添加MySQL Yum Repository：&lt;/h4&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;前往&lt;a href=&quot;http://lib.csdn.net/base/mysql&quot; class=&quot;replace_word&quot; title=&quot;MySQL知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;MySQL&lt;/a&gt;&amp;nbsp;Yum Repository下载页，页面地址：&lt;a href=&quot;http://dev.mysql.com/downloads/repo/yum/&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 102, 153); text-decoration: none; box-sizing: border-box;&quot;&gt;MySQL Yum Repository下载页&lt;/a&gt;，根据服务器版本信息及所需的MySQL版本信息，下载对应的MySQL Yum Repository，本服务器是centos7 64位&lt;a href=&quot;http://lib.csdn.net/base/operatingsystem&quot; class=&quot;replace_word&quot; title=&quot;操作系统知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;操作系统&lt;/a&gt;，想要使用MySQL5.7，可用就是：&lt;a href=&quot;http://repo.mysql.com//mysql57-community-release-el7-9.noarch.rpm&quot; style=&quot;color: rgb(51, 102, 153); text-decoration: none; box-sizing: border-box;&quot;&gt;http://repo.mysql.com//mysql57-community-release-el7-9.noarch.rpm&lt;/a&gt;，命令如下：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;wget&amp;nbsp;http://repo.mysql.com//mysql57-community-release-el7-9.noarch.rpmsudo&amp;nbsp;yum&amp;nbsp;-y&amp;nbsp;localinstall&amp;nbsp;mysql57-community-release-el7-9.noarch.rpm1212&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;②、安装MySQL：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;sudo&amp;nbsp;yum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;mysql-devel&amp;nbsp;mysql-community-server11&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;③、启动MySQL：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;sudo&amp;nbsp;service&amp;nbsp;mysqld&amp;nbsp;start11&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;④、修改root密码，创建用户：&lt;/h4&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;PS.MySQL5.7在启动时会创建一个随机密码，查看设定的root密码：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;sudo&amp;nbsp;grep&amp;nbsp;&amp;#39;temporary&amp;nbsp;password&amp;#39;&amp;nbsp;/var/log/mysqld.log11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;找到显示结果中的密码提示：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;&amp;nbsp;[Note]&amp;nbsp;A&amp;nbsp;temporary&amp;nbsp;password&amp;nbsp;is&amp;nbsp;generated&amp;nbsp;for&amp;nbsp;root@localhost:&amp;nbsp;xxxxx11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;：&lt;/code&gt;之后的就是系统分配的随机密码，根据随机密码登录MySQL：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;mysql&amp;nbsp;-uroot&amp;nbsp;-pxxxxx11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;登录MySQL之后使用命令行修改root密码：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;ALTER&amp;nbsp;USER&amp;nbsp;&amp;#39;root&amp;#39;@&amp;#39;localhost&amp;#39;&amp;nbsp;IDENTIFIED&amp;nbsp;BY&amp;nbsp;&amp;#39;MyNewPass4!&amp;#39;;11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;设置密码的时候一定要设置一个强密码，如果不是强密码，会有如下提示：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;ERROR&amp;nbsp;1819&amp;nbsp;(HY000):&amp;nbsp;Your&amp;nbsp;password&amp;nbsp;does&amp;nbsp;not&amp;nbsp;satisfy&amp;nbsp;the&amp;nbsp;current&amp;nbsp;policy&amp;nbsp;requirements11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;关于密码强度判断及强密码的规则，官方有以下说明：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;Note\r\nMySQL&amp;#39;s&amp;nbsp;validate_password&amp;nbsp;plugin&amp;nbsp;is&amp;nbsp;installed&amp;nbsp;by&amp;nbsp;default.&amp;nbsp;This&amp;nbsp;will&amp;nbsp;require&amp;nbsp;that&amp;nbsp;passwords&amp;nbsp;contain&amp;nbsp;at&amp;nbsp;least&amp;nbsp;one&amp;nbsp;upper&amp;nbsp;case&amp;nbsp;letter,&amp;nbsp;one&amp;nbsp;lower&amp;nbsp;case&amp;nbsp;letter,&amp;nbsp;one&amp;nbsp;digit,&amp;nbsp;and&amp;nbsp;one&amp;nbsp;special&amp;nbsp;character,&amp;nbsp;and&amp;nbsp;that&amp;nbsp;the&amp;nbsp;total&amp;nbsp;password&amp;nbsp;length&amp;nbsp;is&amp;nbsp;at&amp;nbsp;least&amp;nbsp;8&amp;nbsp;characters.1212&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;为了方便使用，我们经常会创建任意连接的root用户：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;GRANT&amp;nbsp;ALL&amp;nbsp;PRIVILEGES&amp;nbsp;ON&amp;nbsp;*.*&amp;nbsp;TO&amp;nbsp;&amp;#39;root&amp;#39;@&amp;#39;%&amp;#39;&amp;nbsp;IDENTIFIED&amp;nbsp;BY&amp;nbsp;&amp;#39;MyNewPass4!&amp;#39;&amp;nbsp;WITH&amp;nbsp;GRANT&amp;nbsp;OPTION;11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;刷新权限使命令生效：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;flush&amp;nbsp;privileges;11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;退出MySQL：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;exit11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;接下来修改系统配置，&lt;/p&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;⑤、如果系统的防火墙开着(or behind a hardware firewall or NAT)你必须放开mysql使用的TCP端口，默认是3306;&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;#为public域开放tcp协议的3306端口\r\nfirewall-cmd&amp;nbsp;--zone=public&amp;nbsp;--add-port=3306/tcp&amp;nbsp;--permanent\r\n#为public域添加mysql服务\r\nfirewall-cmd&amp;nbsp;--zone=public&amp;nbsp;--add-service=mysql&amp;nbsp;--permanent\r\n#重启firewall服务\r\nfirewall-cmd&amp;nbsp;--reload123456123456&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;⑥、修改设定，去掉MySQL中大小写敏感：&lt;/h4&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;用root帐号登录后，在&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;/etc/my.cnf&lt;/code&gt;中设置不区分大小写：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;[mysqld]lower_case_table_names=11212&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;PS.&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;lower_case_table_names&lt;/code&gt;参数详解： 0：区分大小写，1：不区分大小写&lt;/p&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;⑦、更新MySQL：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;yum&amp;nbsp;-y&amp;nbsp;update&amp;nbsp;mysql-community-server11&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;⑧、开机启动MySQL：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;sudo&amp;nbsp;chkconfig&amp;nbsp;mysqld&amp;nbsp;on11&lt;/pre&gt;&lt;hr/&gt;&lt;h2 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;3、安装php7：&lt;/h2&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;①、在/usr/local中创建文件夹php7：&lt;/h4&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;mkdir&amp;nbsp;-p&amp;nbsp;/usr/local/php7mkdir&amp;nbsp;~/php7&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;cd&amp;nbsp;~/php71212&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;②、下载并解压php7安装包：&lt;/h4&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;去&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;https://secure.&lt;a href=&quot;http://lib.csdn.net/base/php&quot; class=&quot;replace_word&quot; title=&quot;PHP知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;PHP&lt;/a&gt;.net&lt;/code&gt;选择php7版本，目前发行版是7.1.0，找到php7.1.0的下载包地址进行下载：&amp;nbsp;&lt;br/&gt;国内建议使用&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;http://cn2.php&lt;a href=&quot;http://lib.csdn.net/base/dotnet&quot; class=&quot;replace_word&quot; title=&quot;.NET知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;.NET&lt;/a&gt;/distributions/php-7.1.0.tar.gz&lt;/code&gt;，&amp;nbsp;&lt;br/&gt;国外建议使用&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;https://secure.php&lt;a href=&quot;http://lib.csdn.net/base/dotnet&quot; class=&quot;replace_word&quot; title=&quot;.NET知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;.Net&lt;/a&gt;/distributions/php-7.1.0.tar.gz&lt;/code&gt;，&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;wget&amp;nbsp;https://secure.php.net/distributions/php-7.1.0.tar.gztar&amp;nbsp;-zxvf&amp;nbsp;php-7.1.0.tar.gzcd&amp;nbsp;php-7.1.0/123123&lt;/pre&gt;&lt;h4 style=&quot;margin: 0.8em 0px; padding: 0px; box-sizing: border-box; font-weight: 100; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;③、配置编译php7：&lt;/h4&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Ⅰ、在使用服务器时我会开启并使用SSL，所以编译安装php时会在环境中加入SSL模块，因为openssl1.0.1版本会有心脏滴血漏洞，所以这里需要更换OpenSSL版本&lt;/p&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;①、下载openssl1.0.2版本：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;wget&amp;nbsp;https://www.openssl.org/source/openssl-1.0.2-latest.tar.gz11&lt;/pre&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;②、解压安装包：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;tar&amp;nbsp;-zxvf&amp;nbsp;openssl-1.0.2-latest.tar.gz11&lt;/pre&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;③、编译安装openssl-1.0.2：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;&amp;nbsp;openssl-1.0.2j\r\n./config&amp;nbsp;shared&amp;nbsp;zlibmake&amp;nbsp;dependmake&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;make&amp;nbsp;install12341234&lt;/pre&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;④、修改历史的OpenSSL文件设置备份：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;mv&amp;nbsp;/usr/bin/openssl&amp;nbsp;/usr/bin/openssl.old\r\nmv&amp;nbsp;/usr/include/openssl&amp;nbsp;/usr/include/openssl.old1212&lt;/pre&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;⑤、设置软连接使其使用新的OpenSSL版本 刚刚安装的OpenSSL默认安装在&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;/usr/local/ssl&lt;/code&gt;下：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;ln&amp;nbsp;-s&amp;nbsp;/usr/local/ssl/bin/openssl&amp;nbsp;/usr/bin/opensslln&amp;nbsp;-s&amp;nbsp;/usr/local/ssl/include/openssl&amp;nbsp;/usr/include/openssl1212&lt;/pre&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;⑥、更新动态链接库数据：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;echo&amp;nbsp;&amp;quot;/usr/local/ssl/lib/&amp;quot;&amp;nbsp;&amp;gt;&amp;gt;&amp;nbsp;/etc/ld.so.confexport&amp;nbsp;LD_LIBRARY_PATH=/usr/local/ssl/lib/\r\nldconfig&amp;nbsp;-v123123&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;查看openssl版本&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;openssl version&lt;/code&gt;：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;OpenSSL&amp;nbsp;1.0.2j&amp;nbsp;&amp;nbsp;26&amp;nbsp;Sep&amp;nbsp;201611&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;执行命令查看openssl依赖库版本是否为1.0.2j：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;strings&amp;nbsp;/usr/local/ssl/lib/libssl.so&amp;nbsp;|grep&amp;nbsp;OpenSSL11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Ⅱ、&lt;span style=&quot;color:red&quot;&gt;在编译配置php时需要安装mcrypt，由于版权原因CentOS源没有mcrypt，所以需要手动安装，安装方法有两个&lt;/span&gt;：&amp;nbsp;&lt;br/&gt;①、使用第三方源，这种方法的好处是方便易于管理，坏处就是不安全：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;wget&amp;nbsp;http://www.atomicorp.com/installers/atomic\r\nsh&amp;nbsp;./atomic1212&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在安装时会显示协议说明，需要输入&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;yes&lt;/code&gt;通过，然后直接回车安装源，安装成功之后安装mcrypt：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;yum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;libmcrypt&amp;nbsp;&amp;nbsp;libmcrypt-devel11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;直接安装得到的版本是2.5.8&amp;nbsp;&lt;br/&gt;②、下载源码编译安装(&lt;span style=&quot;color:red&quot;&gt;推荐&lt;/span&gt;)：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;wget&amp;nbsp;http://jaist.dl.sourceforge.net/project/mcrypt/Libmcrypt/2.5.8/libmcrypt-2.5.8.tar.gztar&amp;nbsp;-zxvf&amp;nbsp;libmcrypt-2.5.8.tar.gzcd&amp;nbsp;libmcrypt-2.5.8/\r\n./configure&amp;nbsp;--prefix=/usr/local\r\nmake&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;make&amp;nbsp;install1234512345&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color:red&quot;&gt;PS. 使用这两种方法安装的区别在于编译php时的&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;--with-mcrypt&lt;/code&gt;路径问题&lt;/span&gt;：&lt;/p&gt;&lt;ul style=&quot;box-sizing: border-box; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot; class=&quot; list-paddingleft-2&quot;&gt;&lt;li&gt;&lt;p&gt;使用yum源安装，路径就是&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;--with-mcrypt=/usr/include&lt;/code&gt;；&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;使用源码编译安装，路径就是&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;--with-mcrypt=/usr/local&lt;/code&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Ⅲ、&lt;span style=&quot;color:red&quot;&gt;修改curl的安装，使其支持openssl&lt;/span&gt;：&amp;nbsp;&lt;br/&gt;PS.如果不支持openssl，CURL无法访问https，在php中同样也无法使用CURL访问https：&amp;nbsp;&lt;br/&gt;查看当前CURL版本：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;curl&amp;nbsp;-V11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;可以看到结果如下：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;curl&amp;nbsp;7.29.0&amp;nbsp;(x86_64-redhat-linux-gnu)&amp;nbsp;libcurl/7.29.0&amp;nbsp;NSS/3.19.1&amp;nbsp;Basic&amp;nbsp;ECC&amp;nbsp;zlib/1.2.7&amp;nbsp;libidn/1.28&amp;nbsp;libssh2/1.4.3Protocols:&amp;nbsp;dict&amp;nbsp;file&amp;nbsp;ftp&amp;nbsp;ftps&amp;nbsp;gopher&amp;nbsp;http&amp;nbsp;https&amp;nbsp;imap&amp;nbsp;imaps&amp;nbsp;ldap&amp;nbsp;ldaps&amp;nbsp;pop3&amp;nbsp;pop3s&amp;nbsp;rtsp&amp;nbsp;scp&amp;nbsp;sftp&amp;nbsp;smtp&amp;nbsp;smtps&amp;nbsp;telnet&amp;nbsp;tftp&amp;nbsp;\r\nFeatures:&amp;nbsp;AsynchDNS&amp;nbsp;GSS-Negotiate&amp;nbsp;IDN&amp;nbsp;IPv6&amp;nbsp;Largefile&amp;nbsp;NTLM&amp;nbsp;NTLM_WB&amp;nbsp;SSL&amp;nbsp;libz123123&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;很明显可以看到是NSS模式，不支持openssl，接下来重新安装curl：&lt;/p&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;①、下载、解压缩CURL安装包：&lt;/h6&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;去官网查看curl最新版本已经是7.51.0，并且支持http2，所以建议重新编译CURL并且使之支持http2，为了让 curl 支持 HTTP2 我们需要安装 nghttp2（http2 的 C 语言库），nghttp2的地址：&lt;a href=&quot;http://www.nghttp2.org/&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 102, 153); text-decoration: none; box-sizing: border-box;&quot;&gt;nghttp2&lt;/a&gt;，&lt;a href=&quot;http://lib.csdn.net/base/git&quot; class=&quot;replace_word&quot; title=&quot;Git知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;Git&lt;/a&gt;仓库：&lt;a href=&quot;https://github.com/nghttp2/nghttp2.git&quot; target=&quot;_blank&quot; style=&quot;color: rgb(51, 102, 153); text-decoration: none; box-sizing: border-box;&quot;&gt;nghttp2的git仓库&lt;/a&gt;，使用source方式安装nghttp2：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;git&amp;nbsp;clone&amp;nbsp;https://github.com/nghttp2/nghttp2.gitcd&amp;nbsp;nghttp2\r\nautoreconf&amp;nbsp;-i\r\nautomake\r\nautoconf\r\n./configure\r\nmake&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;make&amp;nbsp;install12345671234567&lt;/pre&gt;&lt;h6 style=&quot;box-sizing: border-box; font-weight: 100; margin: 0.8em 0px; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;a style=&quot;color: rgb(51, 102, 153); box-sizing: border-box;&quot;&gt;&lt;/a&gt;②、安装CURL7.51.0：&lt;/h6&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;wget&amp;nbsp;https://curl.haxx.se/download/curl-7.51.0.tar.gztar&amp;nbsp;-zxvf&amp;nbsp;curl-7.51.0.tar.gz\r\ncd&amp;nbsp;curl-7.51.0/./configure&amp;nbsp;--prefix=/usr/local/curl&amp;nbsp;--without-nss&amp;nbsp;--with-ssl=/usr/local/ssl&amp;nbsp;--with-nghttp2=/usr/local&amp;nbsp;--with-gssapi&amp;nbsp;--with-libmetalink&amp;nbsp;--with-libssh2&amp;nbsp;--enable-tls-srp&amp;nbsp;--enable-sspimake&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;make&amp;nbsp;install\r\necho&amp;nbsp;&amp;#39;/usr/local/lib&amp;#39;&amp;nbsp;&amp;gt;&amp;nbsp;/etc/ld.so.conf.d/local.conf\r\nldconfig\r\nmv&amp;nbsp;/usr/bin/curl&amp;nbsp;/usr/bin/curl.old\r\nln&amp;nbsp;-s&amp;nbsp;/usr/local/curl/bin/curl&amp;nbsp;/usr/bin/123456789123456789&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;现在查看CURL版本&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;curl -V&lt;/code&gt;，显示结果如下：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;curl&amp;nbsp;7.51.0&amp;nbsp;(x86_64-pc-linux-gnu)&amp;nbsp;libcurl/7.51.0&amp;nbsp;OpenSSL/1.0.2j&amp;nbsp;zlib/1.2.7&amp;nbsp;libssh2/1.4.3&amp;nbsp;nghttp2/1.18.0-DEVProtocols:&amp;nbsp;dict&amp;nbsp;file&amp;nbsp;ftp&amp;nbsp;ftps&amp;nbsp;gopher&amp;nbsp;http&amp;nbsp;https&amp;nbsp;imap&amp;nbsp;imaps&amp;nbsp;ldap&amp;nbsp;ldaps&amp;nbsp;pop3&amp;nbsp;pop3s&amp;nbsp;rtsp&amp;nbsp;scp&amp;nbsp;sftp&amp;nbsp;smb&amp;nbsp;smbs&amp;nbsp;smtp&amp;nbsp;smtps&amp;nbsp;telnet&amp;nbsp;tftp\r\nFeatures:&amp;nbsp;IPv6&amp;nbsp;Largefile&amp;nbsp;GSS-API&amp;nbsp;Kerberos&amp;nbsp;SPNEGO&amp;nbsp;NTLM&amp;nbsp;NTLM_WB&amp;nbsp;SSL&amp;nbsp;libz&amp;nbsp;TLS-SRP&amp;nbsp;HTTP2&amp;nbsp;UnixSockets&amp;nbsp;Metalink123123&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;IV：开始编译安装php：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cd&amp;nbsp;~/php7/php-7.1.011&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;PS.内存1G以下请在结尾加上：–disable-fileinfo，&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;./configure&amp;nbsp;--prefix=/usr/local/php7&amp;nbsp;--exec-prefix=/usr/local/php7&amp;nbsp;--bindir=/usr/local/php7/bin&amp;nbsp;--sbindir=/usr/local/php7/sbin&amp;nbsp;--includedir=/usr/local/php7/include&amp;nbsp;--libdir=/usr/local/php7/lib/php&amp;nbsp;--mandir=/usr/local/php7/php/man&amp;nbsp;--with-config-file-path=/usr/local/php7/etc&amp;nbsp;--enable-rpath&amp;nbsp;--enable-bcmath&amp;nbsp;--enable-calendar&amp;nbsp;--enable-debug&amp;nbsp;--enable-exif&amp;nbsp;--enable-fileinfo&amp;nbsp;--enable-filter&amp;nbsp;--enable-fpm&amp;nbsp;--enable-ftp&amp;nbsp;--enable-gd-jis-conv&amp;nbsp;--enable-gd-native-ttf&amp;nbsp;--enable-hash&amp;nbsp;--enable-inline-optimization&amp;nbsp;--enable-json&amp;nbsp;--enable-libxml&amp;nbsp;--enable-maintainer-zts&amp;nbsp;--enable-mbregex&amp;nbsp;--enable-mbstring&amp;nbsp;--enable-mysqlnd&amp;nbsp;--enable-opcache&amp;nbsp;--enable-opcache-file&amp;nbsp;--enable-pcntl&amp;nbsp;--enable-pdo&amp;nbsp;--enable-session&amp;nbsp;--enable-shared&amp;nbsp;--enable-shmop&amp;nbsp;--enable-simplexml&amp;nbsp;--enable-soap&amp;nbsp;--enable-sockets&amp;nbsp;--enable-sysvmsg&amp;nbsp;--enable-sysvsem&amp;nbsp;--enable-sysvshm&amp;nbsp;--enable-wddx&amp;nbsp;--enable-xml&amp;nbsp;--enable-zip&amp;nbsp;--enable-ctype&amp;nbsp;--with-bz2&amp;nbsp;--with-curl=/usr/local/curl&amp;nbsp;--with-fpm-user=www&amp;nbsp;--with-fpm-group=www&amp;nbsp;--with-freetype-dir=/usr/include/freetype2/freetype&amp;nbsp;--with-gd&amp;nbsp;--with-gettext&amp;nbsp;--with-gmp&amp;nbsp;--with-iconv&amp;nbsp;--with-iconv-dir=/usr&amp;nbsp;--with-jpeg-dir=/usr&amp;nbsp;--with-mcrypt=/usr/local&amp;nbsp;--with-mhash&amp;nbsp;--with-mysql-sock=/var/lib/mysql/mysql.sock&amp;nbsp;--with-mysqli=mysqlnd&amp;nbsp;--with-openssl=/usr/local/ssl&amp;nbsp;--with-pdo-mysql=mysqlnd&amp;nbsp;--with-pear&amp;nbsp;--with-png-dir=/usr&amp;nbsp;--with-xmlrpc&amp;nbsp;--with-zlib&amp;nbsp;--with-libxml-dir=/usr&amp;nbsp;11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;安装：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;make&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;make&amp;nbsp;installmake&amp;nbsp;test1212&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;安装完成之后将php的程序引用至/usr/bin中：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;ln&amp;nbsp;-s&amp;nbsp;/usr/local/php7/bin/*&amp;nbsp;/usr/bin/ln&amp;nbsp;-s&amp;nbsp;/usr/local/php7/sbin/*&amp;nbsp;/usr/sbin/1212&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;查看安装的php的版本：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;php&amp;nbsp;-v11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;显示结果如下：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;PHP&amp;nbsp;7.1.0&amp;nbsp;(cli)&amp;nbsp;(built:&amp;nbsp;Dec&amp;nbsp;&amp;nbsp;2&amp;nbsp;2016&amp;nbsp;19:07:57)&amp;nbsp;(&amp;nbsp;ZTS&amp;nbsp;DEBUG&amp;nbsp;)Copyright&amp;nbsp;(c)&amp;nbsp;1997-2016&amp;nbsp;The&amp;nbsp;PHP&amp;nbsp;GroupZend&amp;nbsp;Engine&amp;nbsp;v3.1.0-dev,&amp;nbsp;Copyright&amp;nbsp;(c)&amp;nbsp;1998-2016&amp;nbsp;Zend&amp;nbsp;Technologies123123&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;现在php7已经安装完成，开始设置PHP7的配置文件php.ini，设定php.ini：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;cp&amp;nbsp;php.ini-production&amp;nbsp;/usr/local/php7/etc/php.iniln&amp;nbsp;-s&amp;nbsp;/usr/local/php7/etc/php.ini&amp;nbsp;/etc/php.inicp&amp;nbsp;/usr/local/php7/etc/php-fpm.conf.default&amp;nbsp;/usr/local/php7/etc/php-fpm.confln&amp;nbsp;-s&amp;nbsp;/usr/local/php7/etc/php-fpm.conf&amp;nbsp;/etc/php-fpm.confcp&amp;nbsp;/usr/local/php7/etc/php-fpm.d/www.conf.default&amp;nbsp;/usr/local/php7/etc/php-fpm.d/www.conf1234512345&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Ⅴ、将php编译生成的bin目录添加到当前&lt;a href=&quot;http://lib.csdn.net/base/linux&quot; class=&quot;replace_word&quot; title=&quot;Linux知识库&quot; target=&quot;_blank&quot; style=&quot;color: rgb(223, 52, 52); text-decoration: none; box-sizing: border-box; font-weight: bold;&quot;&gt;Linux&lt;/a&gt;系统的环境变量中：&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;white-space: nowrap; word-wrap: break-word; box-sizing: border-box; position: relative; overflow-y: hidden; overflow-x: auto; margin-top: 0px; margin-bottom: 1.1em; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 5px 5px 5px 60px; font-size: 14px; line-height: 1.45; word-break: break-all; color: rgb(51, 51, 51); background-color: rgba(128, 128, 128, 0.0470588); border: 1px solid rgba(128, 128, 128, 0.0745098); border-radius: 0px;&quot;&gt;echo&amp;nbsp;-e&amp;nbsp;&amp;#39;export&amp;nbsp;PATH=/usr/local/php7/bin:/usr/local/php7/sbin:$PATH\\n&amp;#39;&amp;nbsp;&amp;gt;&amp;gt;&amp;nbsp;/etc/profile&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;source&amp;nbsp;/etc/profile11&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 1.1em; color: rgb(51, 51, 51); font-family: &amp;quot;microsoft yahei&amp;quot;; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Ⅵ、更改配置，使php7支持opcache：&amp;nbsp;&lt;br/&gt;首先更改&lt;code style=&quot;box-sizing: border-box; font-family: &amp;quot;Source Code Pro&amp;quot;, monospace; padding: 2px 4px; font-size: 12.6px; color: rgb(63, 63, 63); background-color: rgba(128, 128, 128, 0.0745098); white-space: nowrap; border-radius: 0px;&quot;&gt;php.ini&lt;/code&gt;，修改&amp;lt;', '1516157520', '151615752023.jpg', '9', '1027', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('13', 'mysql主从复制', 'mysql主从复制', '&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; clear: both; height: auto; overflow: hidden; color: rgb(44, 44, 44); font-family: 宋体, &amp;quot;Arial Narrow&amp;quot;, arial, serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;怎么安装mysql数据库，这里不说了，只说它的主从复制，步骤如下：&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; clear: both; height: auto; overflow: hidden; color: rgb(44, 44, 44); font-family: 宋体, &amp;quot;Arial Narrow&amp;quot;, arial, serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;1、主从服务器分别作以下操作&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;：&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&amp;nbsp; 1.1、版本一致&lt;br/&gt;&amp;nbsp; 1.2、初始化表，并在后台启动mysql&lt;br/&gt;&amp;nbsp; 1.3、修改root的密码&lt;br/&gt;&lt;br/&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;2、修改主服务器master:&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; #vi /etc/my.cnf&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; [mysqld]&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; log-bin=mysql-bin&amp;nbsp;&amp;nbsp; //[必须]启用二进制日志&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; server-id=222 &amp;nbsp; &amp;nbsp; &amp;nbsp;//[必须]服务器唯一ID，默认是1，一般取IP最后一段&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;3、修改从服务器slave:&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; #vi /etc/my.cnf&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; [mysqld]&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; log-bin=mysql-bin&amp;nbsp;&amp;nbsp; //[不是必须]启用二进制日志&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; server-id=226 &amp;nbsp; &amp;nbsp; &amp;nbsp;//[必须]服务器唯一ID，默认是1，一般取IP最后一段&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;4、重启两台服务器的mysql&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; /etc/init.d/mysql restart&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;5、在主服务器上建立帐户并授权slave:&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; #/usr/local/mysql/bin/mysql -uroot -pmttang&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt;GRANT REPLICATION SLAVE ON *.* to &amp;#39;mysync&amp;#39;@&amp;#39;%&amp;#39; identified by &amp;#39;q123456&amp;#39;; //一般不用root帐号，&amp;amp;ldquo;%&amp;amp;rdquo;表示所有客户端都可能连，只要帐号，密码正确，此处可用具体客户端IP代替，如192.168.145.226，加强安全。&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;6、登录主服务器的mysql，查询master的状态&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt;show master status;&lt;br/&gt;&amp;nbsp;&amp;nbsp; +------------------+----------+--------------+------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | File&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; | Position | Binlog_Do_DB | Binlog_Ignore_DB |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +------------------+----------+--------------+------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | mysql-bin.000004 |&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 308 |&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; |&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +------------------+----------+--------------+------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; 1 row in set (0.00 sec)&lt;br/&gt;&amp;nbsp;&amp;nbsp; 注：执行完此步骤后不要再操作主服务器MYSQL，防止主服务器状态值变化&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;7、配置从服务器Slave：&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt;change master to master_host=&amp;#39;192.168.145.222&amp;#39;,master_user=&amp;#39;&lt;span style=&quot;padding: 0px; margin: 0px; line-height: 28px;&quot;&gt;mysync&lt;/span&gt;&amp;#39;,master_password=&amp;#39;q123456&amp;#39;,&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; master_log_file=&amp;#39;mysql-bin.000004&amp;#39;,master_log_pos=308;&amp;nbsp;&amp;nbsp; //注意不要断开，308数字前后无单引号。&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; Mysql&amp;gt;start slave;&amp;nbsp;&amp;nbsp;&amp;nbsp; //启动从服务器复制功能&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;8、检查从服务器复制功能状态：&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt; show slave status\\G&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; *************************** 1. row ***************************&lt;br/&gt;&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Slave_IO_State: Waiting for master to send event&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Master_Host: 192.168.2.222&amp;nbsp; //主服务器地址&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Master_User: mysync &amp;nbsp; //授权帐户名，尽量避免使用root&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Master_Port: 3306 &amp;nbsp; &amp;nbsp;//数据库端口，部分版本没有此行&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Connect_Retry: 60&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Master_Log_File: mysql-bin.000004&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Read_Master_Log_Pos: 600 &amp;nbsp; &amp;nbsp; //#同步读取二进制日志的位置，大于等于Exec_Master_Log_Pos&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Relay_Log_File: ddte-relay-bin.000003&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Relay_Log_Pos: 251&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Relay_Master_Log_File: mysql-bin.000004&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Slave_IO_Running: Yes &amp;nbsp; &amp;nbsp;//此状态必须YES&lt;br/&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; Slave_SQL_Running: Yes &amp;nbsp; &amp;nbsp; //此状态必须YES&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ......&lt;br/&gt;&lt;br/&gt;注：Slave_IO及Slave_SQL进程必须正常运行，即YES状态，否则都是错误的状态(如：其中一个NO均属错误)。&lt;br/&gt;&lt;br/&gt;以上操作过程，主从服务器配置完成。&lt;br/&gt;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;9、主从服务器测试：&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;主服务器Mysql，建立数据库，并在这个库中建表插入一条数据：&lt;br/&gt;&lt;br/&gt;&amp;nbsp; mysql&amp;gt; create database hi_db;&lt;br/&gt;&amp;nbsp; Query OK, 1 row affected (0.00 sec)&lt;br/&gt;&lt;br/&gt;&amp;nbsp; mysql&amp;gt; use hi_db;&lt;br/&gt;&amp;nbsp; Database changed&lt;br/&gt;&lt;br/&gt;&amp;nbsp; mysql&amp;gt;&amp;nbsp; create table hi_tb(id int(3),name char(10));&lt;br/&gt;&amp;nbsp; Query OK, 0 rows affected (0.00 sec)&lt;br/&gt;&amp;nbsp;&lt;br/&gt;&amp;nbsp; mysql&amp;gt; insert into hi_tb values(001,&amp;#39;bobu&amp;#39;);&lt;br/&gt;&amp;nbsp; Query OK, 1 row affected (0.00 sec)&lt;br/&gt;&lt;br/&gt;&amp;nbsp; mysql&amp;gt; show databases;&lt;br/&gt;&amp;nbsp;&amp;nbsp; +--------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | Database&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +--------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | information_schema |&lt;br/&gt;&amp;nbsp;&amp;nbsp; | hi_db &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;|&lt;br/&gt;&amp;nbsp;&amp;nbsp; | mysql &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;|&lt;br/&gt;&amp;nbsp;&amp;nbsp; | test &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +--------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; 4 rows in set (0.00 sec)&lt;br/&gt;&lt;br/&gt;从服务器Mysql查询：&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt; show databases;&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; +--------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | Database &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +--------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | information_schema |&lt;br/&gt;&amp;nbsp;&amp;nbsp; | hi_db &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; | &amp;nbsp; &amp;nbsp; &amp;nbsp; //I&amp;#39;M here，大家看到了吧&lt;br/&gt;&amp;nbsp;&amp;nbsp; | mysql &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; |&lt;br/&gt;&amp;nbsp;&amp;nbsp; | test &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;|&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; clear: both; height: auto; overflow: hidden; color: rgb(44, 44, 44); font-family: 宋体, &amp;quot;Arial Narrow&amp;quot;, arial, serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp;+--------------------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; 4 rows in set (0.00 sec)&lt;br/&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt; use hi_db&lt;br/&gt;&amp;nbsp;&amp;nbsp; Database changed&lt;br/&gt;&amp;nbsp;&amp;nbsp; mysql&amp;gt; select * from hi_tb; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //查看主服务器上新增的具体数据&lt;br/&gt;&amp;nbsp;&amp;nbsp; +------+------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; | id&amp;nbsp;&amp;nbsp; | name |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +------+------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; |&amp;nbsp;&amp;nbsp;&amp;nbsp; 1 | bobu |&lt;br/&gt;&amp;nbsp;&amp;nbsp; +------+------+&lt;br/&gt;&amp;nbsp;&amp;nbsp; 1 row in set (0.00 sec)&lt;br/&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; clear: both; height: auto; overflow: hidden; color: rgb(44, 44, 44); font-family: 宋体, &amp;quot;Arial Narrow&amp;quot;, arial, serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;padding: 0px; margin: 0px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; font-size: 18px;&quot;&gt;&lt;span style=&quot;padding: 0px; margin: 0px; color: rgb(255, 0, 0);&quot;&gt;10、完成：&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; 编写一shell脚本，用nagios监控slave的两个yes（Slave_IO及Slave_SQL进程），如发现只有一个或零个yes，就表明主从有问题了，发短信警报吧。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516157507', '151615750738.jpg', '9', '735', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('14', '用Nginx+Redis实现session共享的均衡负载php操作redis缓存方法分享', 'php操作redis缓存方法分享', '&lt;p style=&quot;margin-top: 1rem; margin-bottom: 1rem; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;PingFang SC&amp;quot;, &amp;quot;Helvetica Neue&amp;quot;, &amp;quot;Hiragino Sans GB&amp;quot;, &amp;quot;Segoe UI&amp;quot;, &amp;quot;Microsoft YaHei&amp;quot;, 微软雅黑, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;php redis缓存操作&lt;/p&gt;&lt;pre class=&quot;hljs php&quot; style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0.5em; font-family: inherit; overflow-x: auto; background-color: rgb(253, 246, 227); color: rgb(101, 123, 131); border: 1px dashed rgb(133, 153, 0); font-size: 13px; line-height: 20px;&quot;&gt;&amp;lt;?php/**\r\n&amp;nbsp;*&amp;nbsp;Redis缓存操作\r\n&amp;nbsp;*&amp;nbsp;@author&amp;nbsp;hxm\r\n&amp;nbsp;*&amp;nbsp;@version&amp;nbsp;1.0\r\n&amp;nbsp;*&amp;nbsp;@since&amp;nbsp;2015.05.04\r\n&amp;nbsp;*/class&amp;nbsp;RCache&amp;nbsp;extends&amp;nbsp;Object&amp;nbsp;implements&amp;nbsp;CacheFace&amp;nbsp;{&amp;nbsp;&amp;nbsp;private&amp;nbsp;$redis&amp;nbsp;=&amp;nbsp;null;&amp;nbsp;//redis对象\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;private&amp;nbsp;$sId&amp;nbsp;&amp;nbsp;=&amp;nbsp;1;&amp;nbsp;&amp;nbsp;//servier服务ID\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;private&amp;nbsp;$con&amp;nbsp;&amp;nbsp;=&amp;nbsp;null;//链接资源\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;初始化Redis\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;Object\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;__construct()\r\n&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(&amp;nbsp;!class_exists(&amp;#39;Redis&amp;#39;)&amp;nbsp;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;throw&amp;nbsp;new&amp;nbsp;QException(&amp;#39;PHP&amp;nbsp;extension&amp;nbsp;does&amp;nbsp;not&amp;nbsp;exist:&amp;nbsp;Redis&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;redis&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Redis();\r\n&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;链接memcahce服务\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@access&amp;nbsp;private\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$key&amp;nbsp;关键字\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$value&amp;nbsp;缓存内容\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;private&amp;nbsp;function&amp;nbsp;connect(&amp;nbsp;$sid&amp;nbsp;)\r\n&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$file&amp;nbsp;=&amp;nbsp;$this-&amp;gt;CacheFile();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;require&amp;nbsp;$file;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!&amp;nbsp;isset($cache)&amp;nbsp;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;throw&amp;nbsp;new&amp;nbsp;QException(&amp;#39;缓存配置文件不存在&amp;#39;.$file);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$server&amp;nbsp;=&amp;nbsp;$cache[$this-&amp;gt;cacheId];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$sid&amp;nbsp;&amp;nbsp;=&amp;nbsp;isset($sid)&amp;nbsp;==&amp;nbsp;0&amp;nbsp;?&amp;nbsp;$this-&amp;gt;sId&amp;nbsp;:&amp;nbsp;$sid;//memcache服务选择\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(&amp;nbsp;!&amp;nbsp;$server[$sid])\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;throw&amp;nbsp;new&amp;nbsp;QException(&amp;#39;当前操作的缓存服务器配置文件不存在&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$host&amp;nbsp;=&amp;nbsp;$server[$sid][&amp;#39;host&amp;#39;];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$port&amp;nbsp;=&amp;nbsp;$server[$sid][&amp;#39;port&amp;#39;];&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;redis-&amp;gt;connect(&amp;nbsp;$host&amp;nbsp;,&amp;nbsp;$port&amp;nbsp;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;catch&amp;nbsp;(Exception&amp;nbsp;$e)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;exit(&amp;#39;memecache连接失败，错误信息：&amp;#39;.&amp;nbsp;$e-&amp;gt;getMessage());\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;写入缓存\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@access&amp;nbsp;private\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$key&amp;nbsp;关键字\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$value&amp;nbsp;缓存内容\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;set(&amp;nbsp;$key&amp;nbsp;,&amp;nbsp;$value&amp;nbsp;,&amp;nbsp;$sid&amp;nbsp;,&amp;nbsp;$expire&amp;nbsp;=&amp;nbsp;0)\r\n&amp;nbsp;&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;$this-&amp;gt;get($key&amp;nbsp;,&amp;nbsp;$sid);&amp;nbsp;//如果已经存在key值\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(&amp;nbsp;$data&amp;nbsp;)&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$this-&amp;gt;redis-&amp;gt;getset(&amp;nbsp;$key&amp;nbsp;,&amp;nbsp;$value);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$this-&amp;gt;redis-&amp;gt;set(&amp;nbsp;$key&amp;nbsp;,&amp;nbsp;$value);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;读取缓存\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@access&amp;nbsp;private\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$key&amp;nbsp;关键字\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;int&amp;nbsp;&amp;nbsp;&amp;nbsp;$sid&amp;nbsp;选择第几台memcache服务器\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;get(&amp;nbsp;$key&amp;nbsp;,&amp;nbsp;$sid)\r\n&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;connect(&amp;nbsp;$sid&amp;nbsp;);&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$this-&amp;gt;redis-&amp;gt;get($key);\r\n&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;清洗（删除）已经存储的所有的元素\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@access&amp;nbsp;private\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;flush()\r\n&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;connect();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$this-&amp;gt;redis-&amp;gt;flushall();\r\n&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;删除缓存\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@access&amp;nbsp;private\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$key&amp;nbsp;关键字\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;int&amp;nbsp;&amp;nbsp;&amp;nbsp;$sid&amp;nbsp;选择第几台memcache服务器\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;array\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;remove(&amp;nbsp;$key&amp;nbsp;,&amp;nbsp;$sid)\r\n&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;connect();&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$this-&amp;gt;redis-&amp;gt;del($key);\r\n&amp;nbsp;&amp;nbsp;}&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;/**\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;析构函数\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;最后关闭memcache\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\r\n&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;__destruct()\r\n&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($this-&amp;gt;redis)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;redis-&amp;gt;close();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1516157493', '151615749365.jpg', '9', '15074', '1', '2016-12-26 08:50:11');
INSERT INTO `aa_article` VALUES ('15', 'Memcache分布式部署方案', '如何搭建分布式memcache', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 20px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: rgb(255, 255, 255); color: rgb(68, 68, 68); font-family: Georgia, Times, &amp;quot;Times New Roman&amp;quot;, serif; white-space: normal;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;&quot;&gt;基础环境&lt;/strong&gt;&lt;br/&gt;其实基于PHP扩展的Memcache客户端实际上早已经实现，而且非常稳定。先解释一些名词，Memcache是danga.com的一个开源项目，可以类比于MySQL这样的服务，而PHP扩展的Memcache实际上是连接Memcache的方式。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 20px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: rgb(255, 255, 255); color: rgb(68, 68, 68); font-family: Georgia, Times, &amp;quot;Times New Roman&amp;quot;, serif; white-space: normal;&quot;&gt;首先，进行Memcache被安装具体可查看：&lt;br/&gt;&lt;a href=&quot;http://www.ccvita.com/257.html&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(51, 84, 170); text-decoration: none;&quot;&gt;Linux下的Memcache安装&lt;/a&gt;：http://www.ccvita.com/257.html&lt;br/&gt;&lt;a href=&quot;http://www.ccvita.com/258.html&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(51, 84, 170); text-decoration: none;&quot;&gt;Windows下的Memcache安装&lt;/a&gt;：http://www.ccvita.com/258.html；&lt;br/&gt;其次，进行PHP扩展的安装，官方地址是http://pecl.php.net/package/memcache&lt;br/&gt;最后，启动Memcache服务，比如这样&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;prettyprint&quot; style=&quot;margin-top: 0px; margin-bottom: 20px; padding: 5px; border: 1px solid rgb(238, 238, 238); outline: 0px; font-size: 14px; vertical-align: baseline; background-color: rgb(244, 244, 244); font-stretch: normal; font-family: &amp;quot;andale mono&amp;quot;, &amp;quot;lucida console&amp;quot;, monospace; line-height: 1.5; overflow-x: auto; color: rgb(68, 68, 68);&quot;&gt;&lt;table class=&quot;prettyprint-table&quot; width=&quot;802&quot; style=&quot;width: 568px;&quot;&gt;&lt;tbody style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;&quot;&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;&quot; class=&quot;firstRow&quot;&gt;&lt;td class=&quot;source&quot; style=&quot;margin: 0px; padding: 2px 4px; border-width: 0px; border-style: initial; border-color: initial; outline: 0px; vertical-align: middle; background: transparent;&quot;&gt;&lt;span class=&quot;str&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(177, 214, 49); font-style: italic;&quot;&gt;/usr/&lt;/span&gt;&lt;span class=&quot;kwd&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(82, 122, 162);&quot;&gt;local&lt;/span&gt;&lt;span class=&quot;pun&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 134, 19);&quot;&gt;/&lt;/span&gt;&lt;span class=&quot;pln&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(250, 244, 198);&quot;&gt;bin&lt;/span&gt;&lt;span class=&quot;pun&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 134, 19);&quot;&gt;/&lt;/span&gt;&lt;span class=&quot;pln&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; color: rgb(250, 244, 198);&quot;&gt;memcached &lt;/span&gt;&lt;span class=&quot;pun&quot; style=&quot;margin: 0px; padding: 0px; b